import React, { useState, useEffect } from 'react';
import axios from 'axios';
import ProductList from './components/ProductList';
import CategoryMenu from './components/CategoryMenu';
import SearchBar from './components/SearchBar';
import 'antd/dist/reset.css'; // Ant Design CSS reset
import './App.css';

const App = () => {
    const [categories, setCategories] = useState([]);
    const [products, setProducts] = useState([]);
    const [filteredProducts, setFilteredProducts] = useState([]);
    const [selectedCategory, setSelectedCategory] = useState('');
    const [searchTerm, setSearchTerm] = useState('');

    useEffect(() => {
        // Fetch categories
        axios.get('http://localhost:5000/categories')
            .then(response => setCategories(response.data));

        // Fetch products
        axios.get('http://localhost:5000/products')
            .then(response => {
                setProducts(response.data);
                setFilteredProducts(response.data);
            });
    }, []);

    const handleCategoryChange = (category) => {
        setSelectedCategory(category);
        filterProducts(category, searchTerm);
    };

    const handleSearchChange = (term) => {
        setSearchTerm(term);
        filterProducts(selectedCategory, term);
    };

    const filterProducts = (category, term) => {
        let filtered = products;
        if (category) {
            filtered = filtered.filter(product => product.category === category);
        }
        if (term) {
            filtered = filtered.filter(product => product.name.toLowerCase().includes(term.toLowerCase()));
        }
        setFilteredProducts(filtered);
    };

    return (
        <div className="App">
            <h1>Shopping Cart</h1>
            <CategoryMenu categories={categories} onSelectCategory={handleCategoryChange} />
            <SearchBar onSearch={handleSearchChange} />
            <ProductList products={filteredProducts} />
        </div>
    );
};

export default App;
